mapper.select:
 	select si.ssz,si.xmm,si.ida,si.xmrq,si.yyznc,si.yyncsy,si.ncfpzs,si.xmip,
 	si.yyncb,pi.gsjf "pcInfo.gsjf",si.yyqdsj,si.brq,si.ymz,si.sfyx,si.bjsj,si.bjzt
   from serverinfo si
   left join pcinfo pi on pi.ipdz=si.xmip
   where 1=1 

       and si.xmm = #{conditions.xmm,jdbcType=VARCHAR}



		and si.sszid = #{conditions.sszid,jdbcType=INTEGER}



       and si.bjzt = #{conditions.bjzt,jdbcType=VARCHAR}



     and si.bjsj between #{conditions.startTime} and #{conditions.endTime}


   	LIMIT #{start,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

 	select count(*)
 	from serverinfo si
 	where 1=1

       and si.xmm = #{xmm,jdbcType=VARCHAR}



		and si.sszid = #{sszid,jdbcType=INTEGER}



       and si.bjzt = #{bjzt,jdbcType=VARCHAR}


     and si.bjsj between #{startTime} and #{endTime}

 	select si.ssz,si.xmm,si.ida,si.xmrq,si.yyznc,si.yyncsy,si.ncfpzs,si.xmip,
 	si.yyncb,pi.gsjf "pcInfo.gsjf",si.yyqdsj,si.brq,si.ymz,si.sfyx,si.bjsj,si.bjzt
   from serverinfo si
   left join pcinfo pi on pi.ipdz=si.xmip
   where 1=1 

       and si.xmm = #{conditions.xmm,jdbcType=VARCHAR}



		and si.sszid = #{conditions.sszid,jdbcType=INTEGER}



       and si.bjzt != #{conditions.bjzt,jdbcType=VARCHAR}



     and si.bjsj between #{conditions.startTime} and #{conditions.endTime}


   	LIMIT #{start,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

 	select count(*)
 	from serverinfo si
 	where 1=1

       and si.xmm = #{xmm,jdbcType=VARCHAR}



		and si.sszid = #{sszid,jdbcType=INTEGER}



       and si.bjzt != #{bjzt,jdbcType=VARCHAR}


     and si.bjsj between #{startTime} and #{endTime}

select si.ssz,si.xmm,si.ida,si.xmrq,si.yyznc,si.yyncsy,si.ncfpzs,si.xmip, si.yyncb,pi.gsjf "pcInfo.gsjf",si.yyqdsj,si.brq,si.ymz,si.sfyx,si.bjsj,si.bjzt from serverinfo si left join pcinfo pi on pi.ipdz=si.xmip where 1=1
and si.xmm like CONCAT('%',#{conditions.xmm,jdbcType=VARCHAR},'%')
LIMIT #{start,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

select DISTINCT xmm from serverinfo 
where 1=1 

	and sszid = #{sszid,jdbcType=INTEGER}

select si.id, si.xmm, si.bdx, si.bmd5, si.brq, si.xmlj, si.ssz, si.yypid, si.yyznc, si.yyncsy, si.yyncb, si.yyqdr, si.yyqdsj, si.yyzt, si.xmip, si.xmdk, si.sfyx, si.yyxc, si.yycpu, si.xmrq, si.gxsj, si.xmwjm, si.yyjdk, si.ymz, si.jkyz, si.jkcj, si.jkcja, si.nginxhit, si.nginxhita, si.nginxgxsj, si.yylogjk, si.nginxzt, si.yynets, si.ncfpzs, si.yylogwj, si.yyloggjc, si.yyloggjcs, si.yylogzt, si.yylogcz, si.yylogdx, si.ida, si.yylogwjm, si.yybz, si.yylogcssj, si.yyloggjcsz, si.yyfbzt, si.yycjsj,pi.gsjf "pcInfo.gsjf" from serverinfo si left join pcinfo pi on pi.ipdz=si.xmip where 1=1
and si.xmm = #{xmm,jdbcType=VARCHAR}
and si.ssz = #{ssz,jdbcType=VARCHAR}
order by si.yyqdsj desc

