mapper.select:
select * from ( SELECT gb.BRAND_NAME name,SUM(OI.AMOUNT) amount FROM .OMS_ORDER OO INNER JOIN .OMS_ITEM OI ON OI.ORDER_ID = OO.ORDER_ID INNER JOIN .OMS_SKU OS ON OS.COMMERCE_ITEM_ID =OI.COMMERCE_ITEM_ID inner join .gome_dcs_prd_brand pb on pb.product_id=os.product_id inner join .gome_brand gb on gb.brand_id=pb.prd_brand WHERE OI.TYPE = 0
and OO.SUBMITTED_DATE between #{startTime} and #{endTime}
GROUP BY gb.BRAND_NAME, gb.BRAND_CODE ORDER BY SUM(OI.AMOUNT) DESC ) tab WHERE ROWNUM < 6

SELECT * FROM (SELECT gb.BRAND_NAME name, SUM(OI.AMOUNT) amount FROM .OMS_ORDER OO INNER JOIN .OMS_ITEM OI ON OI.ORDER_ID = OO.ORDER_ID INNER JOIN .OMS_SKU OS ON OS.COMMERCE_ITEM_ID =OI.COMMERCE_ITEM_ID inner join .gome_dcs_prd_brand pb on pb.product_id=os.product_id inner join .gome_brand gb on gb.brand_id=pb.prd_brand WHERE OI.TYPE = 0
and OO.SUBMITTED_DATE between #{startTime} and #{endTime}
GROUP BY gb.BRAND_NAME, gb.BRAND_CODE ORDER BY SUM(OI.AMOUNT) DESC) WHERE ROWNUM < 4

SELECT DECODE(OI.MAS_LOC_TYPE, 1,'SMI', 2,'3PP', 3,'G3PP', 6,'B3PP', OI.MAS_LOC_TYPE) name, SUM(OI.QUANTITY) amount FROM .OMS_ORDER OO INNER JOIN .OMS_ITEM OI ON OI.ORDER_ID = OO.ORDER_ID WHERE 1=1
and OO.SUBMITTED_DATE between #{startTime} and #{endTime}
GROUP BY OI.MAS_LOC_TYPE ORDER BY SUM(OI.QUANTITY) DESC

select * from( select ga.city_name name,count(1) amount from .oms_order o inner join .oms_shipping_group sg on sg.order_id=o.order_id inner join .oms_shipping_group_address ga on ga.shipping_group_id=sg.shipping_group_id where o.created_by_order is null
and o.submitted_date between #{startTime} and #{endTime}
group by ga.city_name order by count(1) desc )tab where rownum < 6

SELECT COUNT(1) amount from .ROMS_ORDER ro left join .ROMS_SHIPGROUP rsg ON ro.id = rsg.return_request_id LEFT JOIN tbl_return_order rso ON to_char(rso.order_no)=rsg.return_request_id LEFT JOIN .roms_item ri ON ri.return_request_id=rsg.return_request_id LEFT JOIN .oms_sku os ON os.commerce_item_id=ri.ancestor_item_id LEFT JOIN .gome_sku_shop ss ON ss.sku_id=os.sku_id left join .oms_order oo ON oo.order_id = ro.order_id where ss.shop_no IS NOT NULL AND rsg.gome_state <> rso.status
and ro.created_date between #{startTime} and #{endTime}
and not exists( select 'x' from .ROMS_SHIPGROUP sg inner JOIN tbl_return_order tso ON tso.order_no =sG.Return_Request_Id where tso.status IN ('RRF','RBGC','R2C') and sg.gome_state='CL' and sg.return_request_id=rsg.return_request_id )

SELECT COUNT(1) amount FROM .oms_order DO INNER JOIN .oms_shipping_group SG ON DO.ORDER_ID = SG.ORDER_ID INNER JOIN .oms_g3pp_true_reserve R ON R.Shipping_Group_Id = sg.SHIPPING_GROUP_ID WHERE DO.STATE = 'PROCESSING' and sg.state not in ('NO_PENDING_ACTION','PENDING_MERCHANT_ACTION','REMOVED') AND (sg.GOME_STATE = 'CO' OR sg.GOME_STATE IS NULL) AND DO.CREATED_BY_ORDER IS NULL AND DO.SITE_ID NOT IN ('chongzhiSite', 'gomeLotterySite', 'gomeLotteryAppSite','depositSite','gomeMovieSite','mobileGiftSite','wapGiftSite')
and do.submitted_date between #{startTime} and #{endTime}
AND (R.SAP_RESERVE_STATE <> 'PR' OR R.SAP_RESERVE_STATE IS NULL)

SELECT COUNT(1) amount FROM .oms_order DO INNER JOIN .oms_shipping_group SG ON DO.ORDER_ID = SG.ORDER_ID left join .oms_shop_info oi on oi.shipping_group_id=sg.shipping_group_id WHERE oi.shop_name is null and DO.STATE = 'PROCESSING' and sg.state not in ('NO_PENDING_ACTION','PENDING_MERCHANT_ACTION','REMOVED') AND (sg.GOME_STATE = 'CO' OR sg.GOME_STATE IS NULL) AND DO.CREATED_BY_ORDER IS NULL AND DO.SITE_ID NOT IN ('chongzhiSite', 'gomeLotterySite', 'gomeLotteryAppSite','depositSite','gomeMovieSite','mobileGiftSite','wapGiftSite') AND DO.LAST_MODIFIED_DATE < (SYSDATE - 1 / 24 / 2)
and do.submitted_date between #{startTime} and #{endTime}
AND not exists(select 1 from .ord_header tl where tl.order_num = sg.SHIPPING_GROUP_ID) AND not exists(select 1 from .oms_g3pp_true_reserve r where r.shipping_group_id = sg.shipping_group_id and (r.sap_reserve_state <> 'PR' or r.sap_reserve_state is null))

SELECT COUNT(1) amount FROM .oms_order DO INNER JOIN .oms_shipping_group SG ON DO.ORDER_ID = SG.ORDER_ID left join .oms_shop_info oi on oi.shipping_group_id=sg.shipping_group_id WHERE oi.shop_name is not null and DO.STATE = 'PROCESSING' and sg.state not in ('NO_PENDING_ACTION','PENDING_MERCHANT_ACTION','REMOVED') AND (sg.GOME_STATE = 'CO' OR sg.GOME_STATE IS NULL) AND DO.CREATED_BY_ORDER IS NULL AND DO.SITE_ID NOT IN ('chongzhiSite', 'gomeLotterySite', 'gomeLotteryAppSite','depositSite','gomeMovieSite','mobileGiftSite','wapGiftSite') AND DO.LAST_MODIFIED_DATE < (SYSDATE - 1 / 24 / 2)
and do.submitted_date between #{startTime} and #{endTime}
AND not exists(select 1 from tbl_sub_order tl where to_char(tl.sub_order_id)= sg.SHIPPING_GROUP_ID)

SELECT COUNT(1) amount FROM .oms_order DO INNER JOIN .oms_shipping_group SG ON DO.ORDER_ID = SG.ORDER_ID WHERE DO.STATE = 'PROCESSING' AND SG.STATE='PENDING_MERCHANT_ACTION' AND (sg.GOME_STATE = 'CO' OR sg.GOME_STATE IS NULL) AND DO.CREATED_BY_ORDER IS NULL AND DO.SITE_ID NOT IN ('chongzhiSite', 'gomeLotterySite', 'gomeLotteryAppSite','depositSite','gomeMovieSite','mobileGiftSite','wapGiftSite')
and DO.LAST_MODIFIED_DATE between #{startTime} and #{endTime}
AND not exists(select 1 from .ord_header tl where tl.order_num = sg.SHIPPING_GROUP_ID) AND not exists(select 1 from .oms_g3pp_true_reserve r where r.shipping_group_id = sg.shipping_group_id and r.reserve_state = 'Y' and (r.sap_reserve_state <> 'PR' or r.sap_reserve_state is null) )
